def ask_questions
	puts "A quelle question souhaites-tu que nous repondions ? Saisie le nombre correspondant :"
	puts "1 Combien y a-t-il de handle dans cette array ?"
	puts "2 Quelle est le handle le plus court de cette liste ?"
	puts "3 Combien y-a-t'il de handle contenant 5 caractères"
	puts "4 Combien commencent par une majuscule (première lettre juste après le @) ?"
	puts "5 Trie la liste de handle par ordre alphabétique."
	puts "6 Trie la liste de handle par taille des handle (les plus petits en premiers, les plus grands après)"
	puts "7 Quelle est la position dans l'array de la personne @epenser ?"
	puts "8 Sors-moi une répartition des handle par taille de ces derniers (nombre de handle avec 1 caractère, nombre de handle avec 2 caractères, nombre de handle avec 3 caractères, etc)"
	puts "0 Exit"
	print "> "
	question = gets.chomp.to_i
	if question >= 0 && question <= 8
		return question
	else
		puts "Il fallait saisir un nombre entre 1 et 8"
		return (-1)
	end
end

def array_length(handle)
	puts "-------------------"
	puts "The size of the array is :"
	puts handle.length
	puts "-------------------"
end

def smallest_string(handle)
	puts "-------------------"
	puts "The smallest handle is :"
	size = 100
	handle.each do |string|
		if string.length < size
			size = string.length
		end
	end
	handle.each do |string|
		if string.length == size
			puts string
		end
    end
    puts "-------------------"
end

def handle_size(handle, size)
	puts "-------------------"
	puts "The handle with a size of #{size} are :"
	handle.each do |string|
		if (string.length - 1) == size
			puts string
		end
    end
    puts "-------------------"
end

def upper_case(handle)
	puts "-------------------"
	puts "The handle with an upper case after @ are :"
	handle.each do |string|
		if string[1] == string[1].upcase
			puts string
		end
    end
    puts "-------------------"
end

def answer_question(question, handle)
	if question == 1
		array_length(handle)
	elsif question == 2
		smallest_string(handle)
	elsif question == 3
		handle_size(handle, 5)
	elsif question == 4
		upper_case(handle)
	#elsif question == 5
	#elsif question == 6
	#elsif question == 7
	#else
	end
end

def perform
	handle = create_handle
	while ((question = ask_questions) != 0)
		answer_question(question, handle)
	end
end

perform
